import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom'; // Import useNavigate
import SopraLogo from "../../assets/logo-soprasteria.svg";
import './MainLayout.css';
const MainLayout = ({ children }) => {
  const navigate = useNavigate();
  const [scrolled, setScrolled] = useState(false);
  useEffect(() => {
    const onScroll = () => {
      setScrolled(window.scrollY > 10);
    };
    window.addEventListener("scroll", onScroll);
    return () => window.removeEventListener("scroll", onScroll);
  }, []);
  const [isCollapsed, setIsCollapsed] = useState(false);

  const handleLogout = () => {
    // In a real application, you would clear auth tokens here
    // e.g., localStorage.removeItem('token');
    navigate('/login');
  };
  const toggleSidebar = () => {
  setIsCollapsed(prev => !prev);
};

  

  return (
    <div className="layout-container">
      {/* --- SIDEBAR --- */}
      <nav className={`sidebar ${isCollapsed ? "collapsed" : ""}`}>
  <div className="sidebar-logo">
    <img
      src={SopraIcon}        // red–orange icon SVG
      alt="Sopra icon"
      className="logo-icon"
    />

    <img
      src={SopraLogo}        // full Sopra Steria logo SVG
      alt="Sopra Steria"
      className="logo-full"
    />
  </div>

  {/* rest of sidebar */}
</nav>


      {/* --- MAIN CONTENT AREA --- */}
      <div className="main-content-wrapper">
        <header className={`topbar ${scrolled ? "topbar--blur" : ""}`}>
          <div className="logo-text">
            <span>Smart <b>Automation</b></span>
          </div>

          <div className="user-info">
            <div className="user-details">
              <span className="user-name">Akshat VOHRA</span>
              <span className="user-role">testing</span>
            </div>
            <div className="user-avatar">
              <img src="https://i.pravatar.cc/40?img=1" alt="User Avatar" />
            </div>
            
            {/* --- LOGOUT BUTTON --- */}
            <button className="logout-btn" onClick={handleLogout} title="Sign Out">
              <i className="fa-solid fa-right-from-bracket"></i>
            </button>
          </div>
        </header>

        <main className="page-content">{children}</main>
      </div>
    </div>
  );
};

export default MainLayout;
